import ExpenseItem from "./ExpenseItem";

const ExpensesList = ({expenses, }) => {
  const filteredExpenese = expenses.filter(
    (expense) => expense.date.getFullYear().toString() === selectedYear
  );

  let content = expenses.filter(
    (expense) => expense.date.getFullYear().toString() === selectedYear
  );

  // if there is no filtered expenses diplay p else display expenses content

  if (content.length === 0) {
    content = <p>No items found</p>;
  } else {
    content = filteredExpenese.map((item) => (
      <ExpenseItem
        key={item.title}
        title={item.title}
        amount={item.amount}
        date={item.date}
      />
    ));
  }
};

export default ExpensesList;
