import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import FilterExpenses from "./FilterExpenses";

const Expenses = ({ expenses, filterExpenses, selectedYear }) => {
  const filteredExpenese = expenses.filter(
    (expense) => expense.date.getFullYear().toString() === selectedYear
  );


  let content =  expenses.filter(
    (expense) => expense.date.getFullYear().toString() === selectedYear
  );


  // if there is no filtered expenses diplay p else display expenses content 
  console.log('hello ')
  if (content.length === 0){    
    content = <p>No items found</p>
  }else{
    content = filteredExpenese.map((item) => (
      <ExpenseItem
        key={item.title}
        title={item.title}
        amount={item.amount}
        date={item.date}
      />
    ))
  }



  return (
    <Card>
      <FilterExpenses
        selectedYear={selectedYear}
        filterExpenses={filterExpenses}
      />
      {content}
    </Card>
  );
};

export default Expenses;
