import { useState } from "react";
import ErrorModol from "./ErrorModol";
import Card from "../UI/Card";
import Button from "../UI/Button";
import styles from "./AddUser.module.css";

const AddUser = () => {
  const [userName, setUserName] = useState("");
  const [age, setAge] = useState("");
  const [isValid, setIsValid] = useState(true);

  const newUserHandler = (event) => {
    event.preventDefault();
    console.log(userName, age);

    userValidation();
    ageValidation();
    resetInputs();
  };

  const updateUserNameHandler = (event) => {
    setIsValid(true);
    setUserName(event.target.value);
  };
  const updateAgeNameHandler = (event) => {
    setIsValid(true);
    setAge(event.target.value);
  };
  const resetInputs = () => {
    setUserName("");
    setAge("");
  };



  return (
    <Card className={styles.input}>
      <form onSubmit={newUserHandler}>
        <label htmlFor="username">User name</label>
        <input
          value={userName}
          onChange={updateUserNameHandler}
          id="username"
          type="text"
        />

        <label htmlFor="age">Age</label>
        <input
          value={age}
          onChange={updateAgeNameHandler}
          id="age"
          type="text"
        />
        <Button type="submit" onClick={newUserHandler}>
          Add user
        </Button>

        {!isValid && <ErrorModol message="check your inputs" />}
      </form>
    </Card>
  );
};

export default AddUser;
