import "../../styles/ExpenseForm.css";

const ExpenseForm = () => {
    const titleHandler = (e) =>{
        console.log('changed', e.target.value)
        e.target.value
    }
  return (
    <form >
      <div className="new-expense__control">
        <label>Title</label>
        <input onChange={titleHandler} type="text" />
      </div>
      <div className="new-expense__control">
        <label>Amount</label>
        <input type="nubmer" min="0.01" step="0.01" />
      </div>
      <div className="new-expense__control">
        <label>Amount</label>
        <input type="date" min="2019-01-01" max="2019-12-01" />
      </div>
      <div className="new-expense__actions">
        <button type="sumbit">add expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
